    [Test]
    public void PartOne()
    {
        Assert.Multiple(() =>
        {
            Assert.That(Decoder.EvaluateCalibrationValue("1abc2\npqr3stu8vwx\na1b2c3d4e5f\ntreb7uchet"), Is.EqualTo(142));
        });

        var input = File.ReadAllText($"{Directory.GetParent(Environment.CurrentDirectory)!.Parent!.Parent!.FullName}/input.txt");
        Console.WriteLine(Decoder.EvaluateCalibrationValue(input));
    }

    [Test]
    public void PartTwo()
    {
        Assert.Multiple(() =>
        {
            Assert.That(Decoder.EvaluateCalibrationValue("1abc2\npqr3stu8vwx\na1b2c3d4e5f\ntreb7uchet"), Is.EqualTo(142));
            Assert.That(Decoder.EvaluateCalibrationValueComplex("two1nine\neightwothree\nabcone2threexyz\nxtwone3four\n4nineeightseven2\nzoneight234\n7pqrstsixteen"), Is.EqualTo(281));
        });

        var input = File.ReadAllText($"{Directory.GetParent(Environment.CurrentDirectory)!.Parent!.Parent!.FullName}/input.txt").ReplaceLineEndings("\n");
        Console.WriteLine(Decoder.EvaluateCalibrationValueComplex(input));
    }
